				<?php
				$install_info = "<table>";
				//file_get_contents()
				$check_FGT = file_get_contents("http://google.de");
				if($check_FGT){
					$install_info .= '<tr><td>file_get_contents() installed</td><td><img class="true" src="data:image/png;base64," /><br>';
				} else {
					$install_info .= '<tr><td>file_get_contents() NOT installed</td><td><img class="false" src="data:image/png;base64," /><br>';
					$doNotInstall = true;
				}
				
				//XML
				if(function_exists("simplexml_load_file")){
					$install_info .= '<tr><td>simplexml_load_file() installed</td><td><img class="true" src="data:image/png;base64," /><br>';
				} else {
					$install_info .= '<tr><td>simplexml_load_file() NOT installed</td><td><img class="false" src="data:image/png;base64," /><br>';
					$doNotInstall = true;
				}
				
				//Apache Module
				//mod_rewrite
				$modulesarr = apache_get_modules();
				foreach ($modulesarr as $key => $value){
					$g[] = $value;
				}
				if(in_array('mod_rewrite',$g)){
					$install_info .= '<tr><td>mod_rewrite installed</td><td><img class="true" src="data:image/png;base64," />';
				} else {
					$install_info .= '<tr><td>mod_rewrite NOT installed</td><td><img class="false" src="data:image/png;base64," />';
					$doNotInstall = true;
				}
				
				//MySQLi
				if(extension_loaded('mysqli')){
					$install_info .= '<tr><td>MySQLi installed</td><td><img class="true" src="data:image/png;base64," /><br>';
				} else {
					$install_info .= '<tr><td>MySQLi NOT installed</td><td><img class="false" src="data:image/png;base64," /><br>';
					$doNotInstall = true;
				}
				
				//db configured
				if(file_exists("db_config.php")){
					include "db_config.php";
					$mysql = mysql_connect($db_host, $db_user, $db_pass);
					mysql_select_db($db_name, $mysql);
					if(mysql_error()){
						$doNotInstall = true;
						$install_info .= '<tr><td>Database NOT connected</td><td><img class="false" src="data:image/png;base64," />';
					} else {
						$install_info .= '<tr><td>Database connected</td><td><img class="true" src="data:image/png;base64," />';
					}
				} else {
					$install_info .= '<tr><td>Database NOT connected</td><td><img class="false" src="data:image/png;base64," />';
					$doNotInstall = true;
				}
				
				$install_info .= '</table>';
				echo $install_info;
				
				if(!$doNotInstall){
					?>
					<br><br>
					<center><button onclick="weiter()">Installation starten!</button></center>
					<?php
				} else {
					?>
					<center><h2>Baru CMS kann nicht installiert werden!</h2>Bitte prüfe, ob die oben gelisteten Funktionen installiert und aktiviert sind!</center>
					<?php
				}
